using HECSFramework.Core;using HECSFramework.Unity;

namespace Strategies{	[Documentation(Doc.Strategy, "PredicateNode continue with results of predicate ")]	public class PredicateNode : DilemmaDecision	{		[Connection(ConnectionPointType.In, "<Entity> Owner")]		public GenericNode<Entity> Owner;		[Connection(ConnectionPointType.In, "<Entity> Target")]		public GenericNode<Entity> Target;		public override string TitleOfNode { get; } = "PredicateNode";		[ExposeField]		public PredicateBluePrint PredicateBluePrint;		protected override void Run(Entity entity)		{			if (PredicateBluePrint.GetPredicate.IsReady(Target.Value(entity), Owner.Value(entity)))			{				Positive.Execute(entity);				return;			}			else			{				Negative.Execute(entity);				return;			}		}	}}