using HECSFramework.Core;using UnityEngine.UI;namespace Strategies{	[Documentation(Doc.Strategy, "GetImageFillAmount")]	public class GetImageFillAmount : GenericNode<float>	{		[Connection(ConnectionPointType.In, "<Image> Image")]		public GenericNode<Image> Image;		[Connection(ConnectionPointType.Out, "<float> Out")]		public BaseDecisionNode Out;		public override string TitleOfNode { get; } = "GetImageFillAmount";		public override void Execute(Entity entity)		{		}		public override float Value(Entity entity)		{			return Image.Value(entity).fillAmount;		}	}}